import React, { useState } from 'react';
import { getExpenses } from '../Controllers/expensesController';

const Expenses = () => {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState(null);

  const fetchExpenses = async () => {
    try {
      const data = await getExpenses(startDate, endDate);
      setExpenses(data);
      setError(null);
    } catch (err) {
      setError('No se pudieron obtener los datos.');
    }
  };

  return (
    <div>
      <h1>Reporte de Gastos por Departamento</h1>
      <div>
        <label>Fecha Inicio:</label>
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
        <label>Fecha Fin:</label>
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
        <button onClick={fetchExpenses}>Consultar</button>
      </div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div>
        <h2>Resultados:</h2>
        <ul>
          {expenses.map((expense, index) => (
            <li key={index}>
              {expense.departmentName}: ${expense.totalAmount}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Expenses;
