import React, { useState } from 'react';

const App = () => {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [expenses, setExpenses] = useState([]);

  const fetchExpenses = async () => {
    try {
      const response = await fetch(`/api/expenses?startDate=${startDate}&endDate=${endDate}`);
      const data = await response.json();
      setExpenses(data);
    } catch (error) {
      console.error('Error fetching expenses:', error);
    }
  };

  return (
    <div>
      <h1>Reporte de Gastos por Departamento</h1>
      <div>
        <label>Fecha Inicio:</label>
        <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
        <label>Fecha Fin:</label>
        <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
        <button onClick={fetchExpenses}>Consultar</button>
      </div>
      <div>
        <h2>Resultados:</h2>
        <ul>
          {expenses.map((expense) => (
            <li key={expense.departmentName}>
              {expense.departmentName}: ${expense.totalAmount.toFixed(2)}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
